mapper.insert:
INSERT INTO TBL_BOARD 
(
	  BNO
	, TITLE
	, CONTENT
	, WRITER

) VALUES (
	  (SELECT A
	    FROM (SELECT IFNULL(MAX(BNO)+1, 1) AS A
	            FROM TBL_BOARD) B
	           )
	, #{title}
	, #{content}
	, #{writer}
)

mapper.update:
UPDATE TBL_BOARD
   SET VIEWCNT = VIEWCNT + 1
 WHERE BNO = #{bno}

UPDATE TBL_BOARD
   SET TITLE   = #{title}
     , CONTENT = #{content}
     , WRITER  = #{writer}
 WHERE BNO = #{bno}
